<?phpnamespace Gaoming13\SocialLogin;class Helper{    /**     * 生成带参数的url     * @param string $baseURL     * @param array $keysArr     * @return string     */    public static function combineURL($baseURL, $keysArr)    {        $combined = $baseURL . '?';        $valueArr = array();        foreach($keysArr as $key => $val){            $valueArr[] = "$key=$val";        }        $keyStr = implode('&', $valueArr);        $combined .= ($keyStr);        return $combined;    }    /**     * @param string $name     * @param string $default     * @return string     */    public static function getParam($name, $default = '')    {        return isset($_GET[$name]) ? $_GET[$name] : $default;    }}